import React from 'react';
import { SafeAreaView, TextInput, StyleSheet, Button} from 'react-native';

const InputData = () => {
    const [name, onChangeName] = React.useState(null);
    const [cpf, onChangeCpf] = React.useState(null);
    const [age, onChangeAge] = React.useState(null);
    const [uf, onChangeUf] = React.useState(null);
    const [rendaMensal, onChangeRendaMensal] = React.useState(null);

    const childToParent = () => { 
        this.props.getInput({
            "client":{
            "name":name,
            "cpf":cpf,
            "age":age,
            "uf":uf,
            "renda_mensal":rendaMensal
            }
        })
    }

    return (

        <SafeAreaView>
            <TextInput
                style={styles.input}
                onChangeText={onChangeName}
                value={name}
                placeholder="Example name"
            />
            <TextInput
                style={styles.input}
                onChangeText={onChangeCpf}
                value={cpf}
                placeholder="123.456.789-10"
            />
            <TextInput
                style={styles.input}
                onChangeText={onChangeAge}
                value={age}
                placeholder="00"
                keyboardType="numeric"
            />
            <TextInput
                style={styles.input}
                onChangeText={onChangeUf}
                value={uf}
                placeholder="UF"
            />

            <TextInput
                style={styles.input}
                onChangeText={onChangeRendaMensal}
                value={rendaMensal}
                placeholder="3000"
                keyboardType="numeric"
            />

            <Button primary onClick={childToParent} title="Ver emprestimos"/>
        </SafeAreaView>

        
    );
}

const styles = StyleSheet.create({
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
  },
});

export default InputData;
