import React,{Component} from 'react';
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View } from 'react-native';
import axios from 'axios';
import api from './src/services/Api';
import InputData from './src/inputData';
import { FlatList } from 'react-native-web';


const JSON_EXAMPLE = {
  "client":{
      "name":"teste teste",
      "cpf":"123.456.789-10",
      "age":29,
      "uf":"SP",
      "renda_mensal":3000
  }
}


export default function App(){

  // constructor(props) {
  //   super(props);
  //   this.state = {
  //     cliente:{},
  //     produtosEmprestimo:[]
  //   }
  // }

  const [client, setClient] = React.useState(null);
  const [produtosEmprestimo, setProdutosEmprestimo] = React.useState(null);

  const getDataFromApi = (clientData) => {
    var axios = require('axios');
    var data = JSON.stringify(clientData);

    var config = {
      method: 'post',
      url: 'http://localhost:3000/api',
      headers: { 
        'Content-Type': 'application/json'
      },
      data : data
    };

    axios(config)
    .then((response) => {
      // this.setState({
      //   cliente:response.data.cliente,
      //   produtosEmprestimo:response.data.produtos_emprestimo
      // })
      setClient(response.data.cliente);
      setProdutosEmprestimo(response.data.produtos_emprestimo);
      console.log(JSON.stringify(response.data));

    })
    .catch((error) => {
      console.log(error);
    });
  }




  return (
    <View style={styles.container}>
      <Text>Open up App.js to start working on your app!</Text>
      <InputData inputs={getDataFromApi}/>
      <FlatList
        data={produtosEmprestimo}
        renderItem={({item}) => <Text>{item.tipo_emprestimo} : {item.taxa}</Text>}
      />

      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
